    <div
      className="ui card"
      key={key}

    >
      <div className="image">
        <img alt="product!" src={image} />
      </div>
      <div className="content">
        <div className="header">
          {name}

        </div>
        <div className="meta text-wrap">
          <small>{description}</small>
        </div>
      </div>
