//component to add products to json
//Gloria


import React from "react";

function AddProduct({addProduct}){

    const [name , setName] = useState('')
    const [price , setPrice] = useState('')
    const [image, setImage] = useState('')    
    const [description , setdescription] = useState('')
    const [category , setCategory] = useState('')
    const [rating , setRating] = useState('')
    const [count, setCount] = useState('')




     const handleAmountChange =(event) =>{
        setAmount(event.target.value)
     }

     const handleChange = (event) =>{
        event.preventDefault()
        const newProduct ={
            id: uuid (),
            product:product,
            description:description,
            category:category,
        }
        fetch('http://localhost:8002/products',{
        method:'POST',
        headers:{
            'Content-Type' : 'application/json'
        },
        body:JSON.stringify(newProduct),
     })
     .then((resp)=> resp.json())
     .then((newProduct)=> console.log(newProduct))
    }
    return(
        <div className = 'ui segment' onSubmit = {handleChange}>
         <form className = 'ui form'>
            <div className = 'inline fields'>
            <input type ='text'
                name ='name'
                value = {name}
                onChange ={(e) => setName(e.target.value)}

                />
            <input type ='text'
                name ='price'
                value = {price}
                onChange ={(e) => setPrice(e.target.value)}

                />
             <input type ='text'
                name ='image'
                value = {image}
                onChange ={(e) => setImage(e.target.value)}

                />
            <input type ='text'
                name ='description'
                placeholder = 'Description'
                value = {description}
                onChange ={(e) => setDescription(e.target.value) }

                />
            <input type ='text'
                name ='category'
                placeholder = 'Category'
                value = {category}
                onChange ={() => setCategory(e.target.value)}

                />
            <input type ='text'
                name ='amount'
                placeholder = 'Amount'
                step = '0.01'
                onChange ={handleAmountChange}

                />
                </div>
                <button className = 'ui button' type ='submit'></button>
             </form>
             </div>
    )
    }
    export default AddProduct;
